CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."EventPerson"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "eventId" UUID NOT NULL, "attendeeId" UUID, "name" Text NOT NULL, "affiliation" Text, "roleName" text NOT NULL, "originatingDataId" uuid, PRIMARY KEY ("id") , FOREIGN KEY ("eventId") REFERENCES "public"."Event"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("attendeeId") REFERENCES "public"."Attendee"("id") ON UPDATE cascade ON DELETE set null, FOREIGN KEY ("roleName") REFERENCES "public"."EventPersonRole"("name") ON UPDATE cascade ON DELETE restrict, FOREIGN KEY ("originatingDataId") REFERENCES "public"."OriginatingData"("id") ON UPDATE cascade ON DELETE restrict, UNIQUE ("eventId", "name", "affiliation"), UNIQUE ("eventId", "attendeeId", "roleName"));
